import React from "react";
import "./Notifications.css";

export function Notifications() {
    const click = () => {console.log('Close button has been clicked')}
    return (
    <div className="Notifications" style={{ padding: '10px', border: '1px do #ccc' }}>
        <p>Here is the list of notifications</p>
        <button onClick={click} style={{ position: 'absolute', top: '1rem', right: '1rem' }} aria-label="Close">X</button>
    </div>
    )
};
